var mongoose = require("../dataBase").mongoose;

var userSchema = new mongoose.Schema({
    username: "string",
    password: "string",
});

exports.userModel = mongoose.model("user", userSchema, "luoliang");    //第一个users是模型的名称，第二个UserSchema是上面模型的结构，第三个users是数据库中的一个集合