var userModel = require("./model/userModel").userModel;

//µÇÂ¼
exports.userNameAndPassword = function(username, password, callback) {
    userModel.count({
        username: username,
        password: password
    }, function(err, data) {
        callback(data);
    })
};

//×¢²á
exports.regist = function(username,password,callback){
    new userModel({
        username:username,
        password:password
    }).save(function(err,data){
        callback(data);
    })
}